function startCarousel(){const t=document.querySelector("#carouselTwo");isElementInViewport(t)&&t.setAttribute("data-bs-ride","carousel")}function addAnimateToElements(){document.querySelectorAll("[data-bs-animation]").forEach(t=>{isElementInViewport(t)&&addAnimation(t)})}function addAnimation(t){t.classList.add(t.getAttribute("data-bs-animation")),t.removeAttribute("data-bs-animation")}function isElementInViewport(t){let e=t.getBoundingClientRect();return e.top<=0&&e.bottom>=0||e.bottom>=(window.innerHeight||document.documentElement.clientHeight)&&e.top<=(window.innerHeight||document.documentElement.clientHeight)||e.top>=0&&e.bottom<=(window.innerHeight||document.documentElement.clientHeight)}document.addEventListener("DOMContentLoaded",function(){addAnimateToElements(),startCarousel(),window.addEventListener("scroll",function(){addAnimateToElements(),startCarousel()})});